mapper.update:
update ReviewBoard set title=#{title, jdbcType=VARCHAR},
content=#{content, jdbcType=VARCHAR}, xpos=#{xpos,
jdbcType=VARCHAR}, ypos=#{ypos, jdbcType=VARCHAR}
where bno=#{bno}

update ReviewBoard
set replyCnt=replyCnt+#{amount}
where bno=#{bno}

update ReviewBoard
set readCount=readCount+1
where bno=#{bno}
